import React, {useState, useEffect} from 'react';
import socket from './Socket';

const Chat = ({username}) => {

    const [mensaje, setMensaje] = useState('');
    const[mensajes, setMensajes] = useState([]);

    const[trucks, setTrucks] = useState([]);
    const[positions, setPosition] = useState([[]])


    useEffect(() => {
        console.log('recibiendo mensajes')
        socket.on('CHAT', ({date, message, name}) => {
            console.log(name+':'+message + '->('+date+')');
            setMensajes(mensajes.concat({date, message, name}));
        });

        return () => {
            socket.off('CHAT');
      };
    },[mensajes]);


    useEffect(() => {
        console.log('Recibiendo info de TRUCKS');
        socket.on('TRUCKS', truck =>{setTrucks(trucks.concat(truck))});
        return () => {socket.off()};
    },[trucks]);

    useEffect(() => {
        socket.on( 'POSITION', ({code, position}) => {setPosition(positions.concat({code, position}))});
    });

    useEffect(() => {
        socket.on('FAILURE', ({code, source}) => { 
            let truck  = trucks.find(element => element.code === code);
            if (typeof(truck) === undefined){
                return "Truck not found";
            }
            else{
                console.log('Actualizando el valor de truck:' + code);
                truck.status = source;
            }
        });
    });

    useEffect(() => {
        socket.on('FIX', (code) =>{
            let truck  = trucks.find(element => element.code === code);
            if (typeof(truck) === undefined){
                return "Truck not found";
            }
            else{
                truck.status = 'Ok';
            }
        }); 
        return () => {socket.off()}
    });


    const submit = (e) => {
        e.preventDefault();
        console.log({message : mensaje, name : username})
        socket.emit('CHAT', {message : mensaje, name : username})
    }


    return (

        <div>
            <div><h2>Chat</h2></div>
            <div className="chat"> 
                {mensajes.map((e,i) => <div  key={i}> 
                    <div>({e.date}) -- {e.name}: {e.message}</div>
                </div>)}
            </div>
            <form onSubmit={submit}>
                <label htmlFor=""> Escriba su mensaje : 
                </label>
                <textarea name="" id="" cols="30" value={mensaje} onChange={e => setMensaje(e.target.value)}>

                </textarea>
            <button> Enviar mensaje</button>


            </form>

        </div>
    )
}

export default Chat;
