import React, {useState, useEffect} from 'react';
import socket from './Socket';
import { MapContainer, TileLayer, Polyline, Tooltip, Marker} from 'react-leaflet';
import './Map.css';

const Info = () => {

    const[trucks, setTrucks] = useState([]);
    const[positions, setPosition] = useState([[]]);


    useEffect(() => {
        console.log('Recibiendo info de TRUCKS');
        socket.on('TRUCKS', truck =>{
            setTrucks(trucks.concat(truck));
        });
        return () => {socket.off()};
    },[trucks]);

    useEffect(() => {
        socket.on( 'POSITION', ({code, position}) => {setPosition(positions.concat({code, position}))});
    });

    useEffect(() => {
        socket.on('FAILURE', ({code, source}) => { 
            let truck  = trucks.find(element => element.code === code);
            if (typeof(truck) === undefined){
                return "Truck not found";
            }
            else{
                console.log('Actualizando el valor de truck:' + code);
                truck.status = source;
            }
        });
    });

    useEffect(() => {
        socket.on('FIX', (code) =>{
            console.log('solucionando el problema de: ' + code);
            let truck  = trucks.find(element => element.code === code);
            if (typeof(truck) === undefined){
                return "Truck not found";
            }
            else{
                truck.status = 'Ok';
            }
        });
    });

    const truckInfo = (code) => {
        let truck  = trucks.find(element => element.code === code);
        if (typeof(truck) === undefined){
            return "Truck not found";
        }
        return (<div>
            <p>'Truck:' {truck.truck}</p><br />
            <p>'Code: {truck.code}</p><br />
            <p>'Origin: '{truck.origin} -- Destination: {truck.destination}</p><br />
            <p>'Status: '{truck.status}</p> <br />
        </div>)   
    };





    return (

        <div>
            <div><h2>Trucks Information:</h2></div>
            <div className="info"> 
                {trucks.map((e,i) => <div  key={i}> 
                    <div>Code: {e.code} - origin: {e.origin} - destination: {e.destination} - truck: {e.truck} - status: {e.status}<br /><br /></div>
                </div>)}
            </div>
            <div>
            <MapContainer center={[0,0]} zoom={3} scrollWheelZoom={false}>
                <TileLayer
                attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                />

                {trucks.map((dat) => {
                    return (
                        <div key={dat.code}>
                        <Polyline pathOptions={{color:'black'}} positions={[dat.origin, dat.destination]} />
                        </div>
                    )
                })}

                {positions.map((data) => {
                    return (

                        <div key={data.position}>
                            <Marker center={data.position} radius={100}>
                                <Tooltip>
                                    {truckInfo(data.code)}                                
                                </Tooltip>
                            </Marker>
                        </div>
                    )})}

            </MapContainer>
            </div>




        </div>

    )
}

export default Info;
